export class BlogPost extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open",delegatesFocus:!0}),this.render()}connectedCallback(){console.log("mouted")}disconnectedCallback(){console.log("unmouted")}static get observedAttributes(){return["title","description","link"]}set data(t){console.log("===",t),this.titleElement.textContent=t.title||this.titleElement.textContent,this.descriptionElement.textContent=t.description||this.descriptionElement.textContent,this.linkElement.setAttribute("href",t.link||this.linkElement.href)}attributeChangedCallback(t,e,n){switch(console.log("atrributeChangedCallback"),t){case"title":this.titleElement.textContent=n||"";break;case"description":this.descriptionElement.textContent=n||"";break;case"link":this.linkElement.setAttribute("href",n||"")}}render(){this.shadowRoot.innerHTML='\n      <div class="blog-post">\n        <h2></h2>\n        <p class="desc">\n          \n        </p>\n        <a href="">learn more</a>\n      </div>\n    ',this.titleElement=this.shadowRoot.querySelector("h2"),this.descriptionElement=this.shadowRoot.querySelector(".desc"),this.linkElement=this.shadowRoot.querySelector("a")}}